using System;\nusing System.Collections.Generic;\nusing System.Security.Cryptography;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        // Size of the key, 6\n        HashSet<string> set = new HashSet<string>();\n        int clashes = 0;\n        int totalAttempts = 1000000;\n\n        for (int n = 0; n < totalAttempts; n++)\n        {\n            string uid = GenerateUID(6);\n\n            if (set.Contains(uid))\n            {\n                clashes++;\n                Console.WriteLine("clash: (" + n + ")" + uid);\n            }\n\n            set.Add(uid);\n        }\n\n        Console.WriteLine("Clashes: " + clashes);\n        Console.ReadLine();\n    }\n\n    static string GenerateUID(int length)\n    {\n        const string chars = "abcdefghijklmnopqrstuvwxyz0123456789";\n        char[] uid = new char[length];\n        using (RNGCryptoServiceProvider rng = new RNGCryptoServiceProvider())\n        {\n            byte[] randomBytes = new byte[length];\n            rng.GetBytes(randomBytes);\n\n            for (int i = 0; i < length; i++)\n            {\n                uid[i] = chars[randomBytes[i] % chars.Length];\n            }\n        }\n        return new string(uid);\n    }\n}