using System;\nusing System.Data;\nusing System.Data.OleDb;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string csvFilePath = "path_to_your_csv_file.csv";\n        DataTable dataTable = GetDataTableFromCsv(csvFilePath);\n\n        // Set original column names\n        string[] originalColumnNames = GetOriginalColumnNames(csvFilePath);\n        for (int i = 0; i < originalColumnNames.Length; i++)\n        {\n            dataTable.Columns[i].ColumnName = originalColumnNames[i];\n        }\n\n        // Display DataTable column names\n        foreach (DataColumn column in dataTable.Columns)\n        {\n            Console.WriteLine(column.ColumnName);\n        }\n    }\n\n    static DataTable GetDataTableFromCsv(string path)\n    {\n        DataTable dt = new DataTable();\n        string connString = $"Provider=Microsoft.Jet.OLEDB.4.0;Data Source={Path.GetDirectoryName(path)};Extended Properties=\"Text;HDR=Yes;FMT=Delimited\"";\n\n        using (OleDbConnection conn = new OleDbConnection(connString))\n        {\n            conn.Open();\n            string query = $"SELECT * FROM [{Path.GetFileName(path)}]";\n            OleDbDataAdapter adapter = new OleDbDataAdapter(query, conn);\n            adapter.Fill(dt);\n        }\n\n        return dt;\n    }\n\n    static string[] GetOriginalColumnNames(string path)\n    {\n        using (StreamReader sr = new StreamReader(path))\n        {\n            // Read the first line to get the header\n            string headerLine = sr.ReadLine();\n            return headerLine?.Split(',');\n        }\n    }\n}