<%@ Application Language="C#" %>\n\n<script runat="server">\n    void Session_Start(Object sender, EventArgs e)\n    {\n        // Log session start to the database\n        LogSessionEvent(Session.SessionID, "start");\n    }\n\n    void Session_End(Object sender, EventArgs e)\n    {\n        // Log session end to the database\n        LogSessionEvent(Session.SessionID, "end");\n    }\n\n    private void LogSessionEvent(string sessionId, string eventType)\n    {\n        string connectionString = System.Configuration.ConfigurationManager.ConnectionStrings["YourConnectionString"].ConnectionString;\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            string query = "INSERT INTO SessionLog (SessionID, EventType, EventTime) VALUES (@SessionID, @EventType, @EventTime)";\n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                command.Parameters.AddWithValue("@SessionID", sessionId);\n                command.Parameters.AddWithValue("@EventType", eventType);\n                command.Parameters.AddWithValue("@EventTime", DateTime.Now);\n\n                connection.Open();\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n</script>