// Main Process Code\nusing System;\nusing System.Diagnostics;\nusing System.IO.Pipes;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private Panel panel;\n\n    public MainForm()\n    {\n        panel = new Panel { Dock = DockStyle.Fill };\n        Controls.Add(panel);\n        \n        StartSubProcess();\n    }\n\n    private void StartSubProcess()\n    {\n        Process subProcess = new Process();\n        subProcess.StartInfo.FileName = "SubProcessApp.exe";\n        subProcess.StartInfo.Arguments = panel.Handle.ToString();\n        subProcess.Start();\n\n        NamedPipeServerStream serverStream = new NamedPipeServerStream("SubProcessPipe", PipeDirection.In);\n        serverStream.WaitForConnection();\n        \n        using (StreamReader reader = new StreamReader(serverStream))\n        {\n            string message = reader.ReadLine();\n            // Handle messages from sub-process\n        }\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MainForm());\n    }\n}\n\n// Sub-Process Code\nusing System;\nusing System.IO.Pipes;\nusing System.Runtime.InteropServices;\nusing System.Windows.Forms;\n\npublic class SubProcessForm : Form\n{\n    [DllImport("user32.dll")]\n    static extern IntPtr SetParent(IntPtr hWndChild, IntPtr hWndNewParent);\n\n    public SubProcessForm(string parentHandle)\n    {\n        IntPtr parentHwnd = new IntPtr(Convert.ToInt32(parentHandle));\n        SetParent(this.Handle, parentHwnd);\n\n        NamedPipeClientStream clientStream = new NamedPipeClientStream(".", "SubProcessPipe", PipeDirection.Out);\n        clientStream.Connect();\n\n        using (StreamWriter writer = new StreamWriter(clientStream))\n        {\n            writer.WriteLine("Hello from SubProcess");\n        }\n    }\n\n    [STAThread]\n    static void Main(string[] args)\n    {\n        if (args.Length > 0)\n        {\n            Application.Run(new SubProcessForm(args[0]));\n        }\n    }\n}