static void Main()\n{\n    var ranges = new List<Range<int>>\n    {\n        Range<int>.Create(1, 5),\n        Range<int>.Create(6, 10),\n        Range<int>.Create(12, 15)\n    };\n\n    Func<int, int, bool> isAdjacent = (end, start) => end + 1 == start;\n\n    var mergedRanges = ranges.MergeAdjacent(isAdjacent);\n\n    foreach (var range in mergedRanges)\n    {\n        Console.WriteLine($"Start: {range.Start}, End: {range.End}");\n    }\n}