using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Person\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\npublic class Detail\n{\n    public int PersonId { get; set; }\n    public int Value { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        var list1 = new List<Person>\n        {\n            new Person { Id = 1, Name = "Alice" },\n            new Person { Id = 2, Name = "Bob" },\n            new Person { Id = 3, Name = "Carol" },\n            new Person { Id = 4, Name = "Dave" }\n        };\n\n        var list2 = new List<Detail>\n        {\n            new Detail { PersonId = 1, Value = 15 },\n            new Detail { PersonId = 1, Value = 19 },\n            new Detail { PersonId = 2, Value = 5 },\n            new Detail { PersonId = 2, Value = 7 },\n            new Detail { PersonId = 2, Value = 20 },\n            new Detail { PersonId = 4, Value = 16 }\n        };\n\n        var list1Ids = list1.Select(p => p.Id).ToList();\n        var list2Ids = list2.Select(d => d.PersonId).Distinct().ToList();\n\n        var missingIds = list1Ids.Except(list2Ids).ToList();\n\n        foreach (var id in missingIds)\n        {\n            list2.Add(new Detail { PersonId = id, Value = 0 });\n        }\n\n        // Output the updated List2\n        foreach (var detail in list2)\n        {\n            Console.WriteLine($"PersonId: {detail.PersonId}, Value: {detail.Value}");\n        }\n    }\n}