using System;\nusing System.IO;\nusing System.Linq;\nusing System.Net;\nusing RestSharp;\nusing Newtonsoft.Json.Linq;\n\nclass Program\n{\n    private static string apiKey = "YOUR_FLICKR_API_KEY";\n    \n    static void Main(string[] args)\n    {\n        string keyword = "nature"; // Replace with your keyword\n        string imageUrl = GetRandomImageUrl(keyword);\n\n        if (!string.IsNullOrEmpty(imageUrl))\n        {\n            DownloadImage(imageUrl, "random_image.jpg");\n            Console.WriteLine("Image downloaded successfully!");\n        }\n        else\n        {\n            Console.WriteLine("No image found for the given keyword.");\n        }\n    }\n\n    private static string GetRandomImageUrl(string keyword)\n    {\n        var client = new RestClient("https://api.flickr.com");\n        var request = new RestRequest("/services/rest/", Method.GET);\n        request.AddParameter("method", "flickr.photos.search");\n        request.AddParameter("api_key", apiKey);\n        request.AddParameter("text", keyword);\n        request.AddParameter("format", "json");\n        request.AddParameter("nojsoncallback", "1");\n\n        var response = client.Execute(request);\n        if (response.IsSuccessful)\n        {\n            var jsonResponse = JObject.Parse(response.Content);\n            var photos = jsonResponse["photos"]["photo"];\n\n            if (photos != null && photos.Any())\n            {\n                var randomPhoto = photos[new Random().Next(photos.Count())];\n                string farmId = randomPhoto["farm"].ToString();\n                string serverId = randomPhoto["server"].ToString();\n                string photoId = randomPhoto["id"].ToString();\n                string secret = randomPhoto["secret"].ToString();\n\n                return $"https://farm{farmId}.staticflickr.com/{serverId}/{photoId}_{secret}.jpg";\n            }\n        }\n\n        return string.Empty;\n    }\n\n    private static void DownloadImage(string imageUrl, string filePath)\n    {\n        using (WebClient client = new WebClient())\n        {\n            client.DownloadFile(new Uri(imageUrl), filePath);\n        }\n    }\n}