using System;\nusing System.Web.Security;\n\npublic class FormsAuthTimeout\n{\n    public void SetFormsAuthTimeout(int timeoutMinutes)\n    {\n        FormsAuthenticationTicket ticket = new FormsAuthenticationTicket(\n            1,                                         // Ticket version\n            "username",                                // Username associated with ticket\n            DateTime.Now,                              // Date/time issued\n            DateTime.Now.AddMinutes(timeoutMinutes),   // Date/time to expire\n            false,                                     // Persistent cookie\n            "userData",                                // User-data, if any\n            FormsAuthentication.FormsCookiePath);      // Path cookie is valid for\n\n        // Encrypt the ticket\n        string encryptedTicket = FormsAuthentication.Encrypt(ticket);\n\n        // Create the cookie\n        HttpCookie authCookie = new HttpCookie(FormsAuthentication.FormsCookieName, encryptedTicket);\n        authCookie.Expires = ticket.Expiration;\n\n        // Add the cookie to the response\n        HttpContext.Current.Response.Cookies.Add(authCookie);\n    }\n}