public class ProductDTO\n{\n    public int ProductId { get; set; }\n    public string ProductName { get; set; }\n    public int SupplierId { get; set; } // Only include the SupplierId to avoid circular reference\n}\n\npublic class SupplierDTO\n{\n    public int SupplierId { get; set; }\n    public string SupplierName { get; set; }\n    public List<int> ProductIds { get; set; } // Only include ProductIds to avoid circular reference\n}\n\npublic class EntityToDTOMapper\n{\n    public ProductDTO GetProductDTO(Product entity)\n    {\n        return new ProductDTO\n        {\n            ProductId = entity.ProductId,\n            ProductName = entity.ProductName,\n            SupplierId = entity.Supplier.SupplierId\n        };\n    }\n\n    public SupplierDTO GetSupplierDTO(Supplier entity)\n    {\n        return new SupplierDTO\n        {\n            SupplierId = entity.SupplierId,\n            SupplierName = entity.SupplierName,\n            ProductIds = entity.Products.Select(p => p.ProductId).ToList()\n        };\n    }\n}