/// <summary>\n/// Represents a class that wraps around an XML document with a very strictly defined schema.\n/// </summary>\npublic class XmlDocumentWrapper\n{\n    private string _elementValue;\n    private static readonly string ElementValuePattern = "your-regex-pattern-here";\n\n    /// <summary>\n    /// Gets or sets the element value.\n    /// The value must match the following regular expression pattern: your-regex-pattern-here\n    /// </summary>\n    /// <exception cref="ArgumentException">Thrown when the value does not match the required pattern.</exception>\n    public string ElementValue\n    {\n        get => _elementValue;\n        set\n        {\n            if (!System.Text.RegularExpressions.Regex.IsMatch(value, ElementValuePattern))\n            {\n                throw new ArgumentException($"The value must match the pattern: {ElementValuePattern}");\n            }\n            _elementValue = value;\n        }\n    }\n\n    // Additional properties and methods\n}