using System;\nusing System.Configuration;\nusing System.ServiceModel.Configuration;\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        string endpointAddress = GetServiceEndpointAddress("Sc.Neo.Bus.Server.MessageProxy");\n        Console.WriteLine("Endpoint Address: " + endpointAddress);\n    }\n\n    public static string GetServiceEndpointAddress(string serviceName)\n    {\n        Configuration config = ConfigurationManager.OpenExeConfiguration(ConfigurationUserLevel.None);\n        ServiceModelSectionGroup serviceModel = ServiceModelSectionGroup.GetSectionGroup(config);\n\n        if (serviceModel != null)\n        {\n            foreach (ServiceElement service in serviceModel.Services.Services)\n            {\n                if (service.Name == serviceName)\n                {\n                    foreach (ServiceEndpointElement endpoint in service.Endpoints)\n                    {\n                        return endpoint.Address.ToString();\n                    }\n                }\n            }\n        }\n        return null;\n    }\n}