// In the ASPX page\n<asp:Panel ID="panel" runat="server"></asp:Panel>\n<asp:LinkButton ID="btnAddControls" runat="server" OnClick="AddControls_Click">Add Controls</asp:LinkButton>\n\n// In the code-behind (ASPX.cs)\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        ViewState["ControlCount"] = 0;\n    }\n    else\n    {\n        RecreateControls();\n    }\n}\n\nprotected void AddControls_Click(object sender, EventArgs e)\n{\n    int count = (int)ViewState["ControlCount"];\n    count++;\n    ViewState["ControlCount"] = count;\n\n    CreateControl(count);\n}\n\nprivate void RecreateControls()\n{\n    int count = (int)ViewState["ControlCount"];\n    for (int i = 1; i <= count; i++)\n    {\n        CreateControl(i);\n    }\n}\n\nprivate void CreateControl(int index)\n{\n    DropDownList newDropdownlist = new DropDownList();\n    panel.Controls.Add(newDropdownlist);\n\n    CheckBox newChkbox = new CheckBox();\n    panel.Controls.Add(newChkbox);\n\n    TextBox txt = new TextBox();\n    txt.ID = "txtPhoneValue" + index;\n    panel.Controls.Add(txt);\n}