// Assuming 'dataGridView1' is your DataGridView and 'dataSet' is your DataSet\ndataGridView1.DataSource = dataSet.Tables["aTableName"];\n\n// Set the display order of the columns manually\ndataGridView1.Columns["ColumnName1"].DisplayIndex = 0; // First column\ndataGridView1.Columns["ColumnName2"].DisplayIndex = 1; // Second column\ndataGridView1.Columns["ColumnName3"].DisplayIndex = 2; // Third column\n// ... repeat for other columns as needed\n\n// Alternatively, you can use index-based approach\ndataGridView1.Columns[0].DisplayIndex = 0; // First column\ndataGridView1.Columns[1].DisplayIndex = 1; // Second column\ndataGridView1.Columns[2].DisplayIndex = 2; // Third column\n// ... repeat for other columns as needed