using System;\nusing System.IO;\nusing System.Linq;\n\npublic class FileCounter\n{\n    public static int MagicFindFileCount(string directory, string pattern)\n    {\n        return Directory.EnumerateFiles(directory, pattern, SearchOption.AllDirectories).Count();\n    }\n\n    public static void Main()\n    {\n        int componentCount = MagicFindFileCount(@"c:\windows\system32", "*.dll");\n        Console.WriteLine($"Number of .dll files: {componentCount}");\n    }\n}