public class PackageController : Controller\n{\n    private readonly IPackageService _packageService;\n    private readonly IPackageViewModelBuilder _viewModelBuilder;\n\n    public PackageController(IPackageService packageService, IPackageViewModelBuilder viewModelBuilder)\n    {\n        _packageService = packageService;\n        _viewModelBuilder = viewModelBuilder;\n    }\n\n    public ActionResult List()\n    {\n        var packages = _packageService.GetPackagesInQueue();\n        var viewModel = _viewModelBuilder.BuildListViewModel(packages);\n        return View(viewModel);\n    }\n\n    // Other actions\n}