using System;\nusing System.Windows.Forms;\n\nnamespace WinFormsApp\n{\n    public class MainForm : Form\n    {\n        private Button helloButton;\n\n        public MainForm()\n        {\n            // Initialize components\n            InitializeComponent();\n        }\n\n        private void InitializeComponent()\n        {\n            this.helloButton = new Button();\n            this.helloButton.Text = "Click Me!";\n            this.helloButton.Location = new System.Drawing.Point(50, 50);\n            this.helloButton.Click += new EventHandler(this.HelloButton_Click);\n\n            this.Controls.Add(this.helloButton);\n\n            this.Text = "Hello WinForms";\n            this.StartPosition = FormStartPosition.CenterScreen;\n        }\n\n        private void HelloButton_Click(object sender, EventArgs e)\n        {\n            MessageBox.Show("Hello, WinForms!");\n        }\n\n        [STAThread]\n        static void Main()\n        {\n            Application.EnableVisualStyles();\n            Application.SetCompatibleTextRenderingDefault(false);\n            Application.Run(new MainForm());\n        }\n    }\n}