using System;\nusing System.Drawing;\nusing System.IO;\n\npublic class ImageChecker\n{\n    public static bool IsImage(string filePath)\n    {\n        try\n        {\n            using (Image img = Image.FromFile(filePath))\n            {\n                return true;\n            }\n        }\n        catch (OutOfMemoryException)\n        {\n            // Image.FromFile will throw this exception if the file is not a valid image.\n            return false;\n        }\n        catch (FileNotFoundException)\n        {\n            // Handle the case when the file does not exist.\n            return false;\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string[] files = Directory.GetFiles("your_directory_path");\n\n        foreach (string file in files)\n        {\n            if (IsImage(file))\n            {\n                Console.WriteLine($"{file} is an image.");\n                // Perform your copy operation here\n            }\n            else\n            {\n                Console.WriteLine($"{file} is not an image.");\n            }\n        }\n    }\n}