using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string myFile = @"path\to\your\logfile.txt";\n\n        try\n        {\n            using (FileStream fileStream = new FileStream(myFile, FileMode.Open, FileAccess.Read, FileShare.ReadWrite))\n            using (StreamReader reader = new StreamReader(fileStream))\n            {\n                string line;\n                while ((line = reader.ReadLine()) != null)\n                {\n                    // Process the line here\n                    Console.WriteLine(line);\n                }\n            }\n        }\n        catch (IOException ex)\n        {\n            Console.WriteLine("An error occurred while opening the file: " + ex.Message);\n        }\n    }\n}