using System;\nusing System.IO;\nusing System.Threading.Tasks;\nusing Hangfire;\n\npublic class DirectoryCleanupService\n{\n    public void ScheduleDirectoryCleanup()\n    {\n        RecurringJob.AddOrUpdate(() => CleanUpDirectories(), Cron.Daily);\n    }\n\n    public void CleanUpDirectories()\n    {\n        string[] directories = Directory.GetDirectories("path_to_books_directory");\n\n        foreach (string directory in directories)\n        {\n            try\n            {\n                Directory.Delete(directory, true);\n            }\n            catch (Exception ex)\n            {\n                // Handle exceptions (e.g., log the error)\n                Console.WriteLine($"Error deleting directory {directory}: {ex.Message}");\n            }\n        }\n    }\n}