using System;\nusing System.Diagnostics;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class BandwidthCalculator\n{\n    private static async Task<long> GetResponseSizeAsync(HttpClient client, string url)\n    {\n        var response = await client.GetAsync(url);\n        response.EnsureSuccessStatusCode();\n        var responseBody = await response.Content.ReadAsByteArrayAsync();\n        return responseBody.Length;\n    }\n\n    public static async Task<double> CalculateBandwidthAsync(string url)\n    {\n        using (HttpClient client = new HttpClient())\n        {\n            Stopwatch stopwatch = new Stopwatch();\n\n            // Record the start time\n            stopwatch.Start();\n\n            // Measure the size of the response\n            long responseSize = await GetResponseSizeAsync(client, url);\n\n            // Record the end time\n            stopwatch.Stop();\n\n            // Calculate the duration in seconds\n            double durationSeconds = stopwatch.Elapsed.TotalSeconds;\n\n            // Calculate bandwidth in bytes per second\n            double bandwidth = responseSize / durationSeconds;\n\n            return bandwidth;\n        }\n    }\n\n    public static async Task Main(string[] args)\n    {\n        string url = "http://youraspnetpage.com";\n        double bandwidth = await CalculateBandwidthAsync(url);\n        Console.WriteLine($"Bandwidth: {bandwidth} bytes/second");\n    }\n}