using System;\nusing System.Collections.Generic;\nusing System.Web;\n\npublic class SessionManager\n{\n    private const string PageKeyPrefix = "PageSession_";\n\n    // Store data with a unique page key\n    public static void StoreSessionData(string pageKey, object data)\n    {\n        string key = PageKeyPrefix + pageKey;\n        HttpContext.Current.Session[key] = data;\n        HttpContext.Current.Session[key + "_Timestamp"] = DateTime.Now;\n    }\n\n    // Retrieve data using the page key\n    public static object GetSessionData(string pageKey)\n    {\n        string key = PageKeyPrefix + pageKey;\n        HttpContext.Current.Session[key + "_Timestamp"] = DateTime.Now; // Update access time\n        return HttpContext.Current.Session[key];\n    }\n\n    // Remove expired session data\n    public static void CleanupExpiredSessionData(TimeSpan expirationTime)\n    {\n        var keysToRemove = new List<string>();\n        foreach (string key in HttpContext.Current.Session.Keys)\n        {\n            if (key.StartsWith(PageKeyPrefix))\n            {\n                DateTime timestamp = (DateTime)HttpContext.Current.Session[key + "_Timestamp"];\n                if (DateTime.Now - timestamp > expirationTime)\n                {\n                    keysToRemove.Add(key);\n                    keysToRemove.Add(key + "_Timestamp");\n                }\n            }\n        }\n\n        foreach (string key in keysToRemove)\n        {\n            HttpContext.Current.Session.Remove(key);\n        }\n    }\n}