protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Bind your data to the Repeater here\n        Repeater1.DataSource = GetData(); // Replace with your data source\n        Repeater1.DataBind();\n    }\n}\n\nprotected void Repeater1_ItemDataBound(object sender, RepeaterItemEventArgs e)\n{\n    if (e.Item.ItemType == ListItemType.Item || e.Item.ItemType == ListItemType.AlternatingItem)\n    {\n        // Find the ThumbViewer control\n        var thumbViewer = (ThumbViewer)e.Item.FindControl("Th1");\n        if (thumbViewer != null)\n        {\n            // Set the ImageUrl property\n            var dataItem = (YourDataType)e.Item.DataItem; // Replace YourDataType with the actual type of your data item\n            thumbViewer.ImageUrl = $"images/{dataItem.Name}"; // Adjust the property name accordingly\n        }\n    }\n}\n\n// Method to get data (replace with your actual data retrieval logic)\nprivate IEnumerable<YourDataType> GetData()\n{\n    // Your data fetching logic here\n}