using System;\nusing System.Windows.Forms;\n\npublic class PercentFormatter : Form\n{\n    private TextBox textBox;\n    private Label label;\n\n    public PercentFormatter()\n    {\n        textBox = new TextBox { Location = new System.Drawing.Point(15, 15), Width = 100 };\n        label = new Label { Location = new System.Drawing.Point(15, 50), Width = 100 };\n        Button button = new Button { Text = "Format", Location = new System.Drawing.Point(130, 12) };\n        button.Click += FormatAsPercent;\n\n        Controls.Add(textBox);\n        Controls.Add(label);\n        Controls.Add(button);\n    }\n\n    private void FormatAsPercent(object sender, EventArgs e)\n    {\n        if (double.TryParse(textBox.Text, out double value))\n        {\n            label.Text = value.ToString("P");\n        }\n        else\n        {\n            label.Text = "Invalid input";\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.Run(new PercentFormatter());\n    }\n}