using System;\n   using System.IO;\n   using System.Xml.Serialization;\n\n   public class Program\n   {\n       public static void Main()\n       {\n           string xmlResponse = "<ArrayOfLong xmlns:i=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns=\"http://schemas.microsoft.com/2003/10/Serialization/Arrays\"><long>1</long><long>2</long><long>3</long><long>4</long></ArrayOfLong>";\n\n           XmlSerializer serializer = new XmlSerializer(typeof(long[]), new XmlRootAttribute("ArrayOfLong"));\n           using (StringReader reader = new StringReader(xmlResponse))\n           {\n               long[] result = (long[])serializer.Deserialize(reader);\n               foreach (var value in result)\n               {\n                   Console.WriteLine(value);\n               }\n           }\n       }\n   }