using System;\nusing System.Collections.Generic;\nusing System.Net;\nusing System.Net.Mail;\nusing System.Threading.Tasks;\n\npublic class BulkEmailSender\n{\n    private const string SmtpServer = "smtp.yourserver.com";\n    private const int SmtpPort = 587;\n    private const string SmtpUsername = "yourusername";\n    private const string SmtpPassword = "yourpassword";\n\n    public static async Task SendBulkEmailsAsync(List<string> emailAddresses, string subject, string body)\n    {\n        var smtpClient = new SmtpClient(SmtpServer)\n        {\n            Port = SmtpPort,\n            Credentials = new NetworkCredential(SmtpUsername, SmtpPassword),\n            EnableSsl = true,\n        };\n\n        var tasks = new List<Task>();\n\n        foreach (var emailAddress in emailAddresses)\n        {\n            var mailMessage = new MailMessage\n            {\n                From = new MailAddress("no-reply@yourdomain.com"),\n                Subject = subject,\n                Body = body,\n                IsBodyHtml = true,\n            };\n            mailMessage.To.Add(emailAddress);\n\n            tasks.Add(smtpClient.SendMailAsync(mailMessage));\n        }\n\n        await Task.WhenAll(tasks);\n    }\n}\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        var emailAddresses = new List<string> { "test1@example.com", "test2@example.com", "test3@example.com" };\n        string subject = "Promotional Offer";\n        string body = "<h1>Exclusive Offer Just for You!</h1><p>Get 50% off on your next flight.</p>";\n\n        await BulkEmailSender.SendBulkEmailsAsync(emailAddresses, subject, body);\n    }\n}