using System;\nusing System.Data;\nusing System.Data.OleDb;\nusing System.Data.SqlClient;\nusing System.Linq;\n\npublic class DataProcessor\n{\n    public DataSet LoadData(string accessFilePath, string sqlConnectionString)\n    {\n        DataSet dataSet = new DataSet();\n\n        // Load data from Access database\n        string accessConnectionString = $"Provider=Microsoft.ACE.OLEDB.12.0;Data Source={accessFilePath};";\n        using (OleDbConnection accessConnection = new OleDbConnection(accessConnectionString))\n        {\n            string accessQuery = "SELECT * FROM Materials"; // Adjust the query as needed\n            OleDbDataAdapter accessAdapter = new OleDbDataAdapter(accessQuery, accessConnection);\n            DataTable accessTable = new DataTable("AccessData");\n            accessAdapter.Fill(accessTable);\n            dataSet.Tables.Add(accessTable);\n        }\n\n        // Load data from SQL Server database\n        using (SqlConnection sqlConnection = new SqlConnection(sqlConnectionString))\n        {\n            string sqlQuery = "SELECT * FROM MaterialPrices"; // Adjust the query as needed\n            SqlDataAdapter sqlAdapter = new SqlDataAdapter(sqlQuery, sqlConnection);\n            DataTable sqlTable = new DataTable("SqlData");\n            sqlAdapter.Fill(sqlTable);\n            dataSet.Tables.Add(sqlTable);\n        }\n\n        return dataSet;\n    }\n\n    public void ProcessData(DataSet dataSet)\n    {\n        DataTable accessTable = dataSet.Tables["AccessData"];\n        DataTable sqlTable = dataSet.Tables["SqlData"];\n\n        var query = from accessRow in accessTable.AsEnumerable()\n                    join sqlRow in sqlTable.AsEnumerable()\n                    on accessRow.Field<string>("MaterialId") equals sqlRow.Field<string>("MaterialId") // Adjust the column name as needed\n                    select new\n                    {\n                        MaterialId = accessRow.Field<string>("MaterialId"),\n                        AccessData = accessRow.Field<string>("AccessColumn"), // Adjust the column name as needed\n                        SqlData = sqlRow.Field<decimal>("SqlColumn") // Adjust the column name as needed\n                    };\n\n        foreach (var result in query)\n        {\n            Console.WriteLine($"{result.MaterialId}, {result.AccessData}, {result.SqlData}");\n        }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string accessFilePath = @"C:\path\to\accessfile.accdb";\n        string sqlConnectionString = "your_sql_server_connection_string";\n\n        DataProcessor processor = new DataProcessor();\n        DataSet dataSet = processor.LoadData(accessFilePath, sqlConnectionString);\n        processor.ProcessData(dataSet);\n    }\n}