using System;\nusing System.IO;\n\nnamespace YourNamespace\n{\n    public partial class YourPage : System.Web.UI.Page\n    {\n        protected void Page_Load(object sender, EventArgs e)\n        {\n            if (!IsPostBack)\n            {\n                string filePath = Server.MapPath("~/path/to/your/file.txt"); // Adjust the path accordingly\n                if (File.Exists(filePath))\n                {\n                    try\n                    {\n                        string fileContent = File.ReadAllText(filePath);\n                        lblFileContent.Text = fileContent;\n                    }\n                    catch (Exception ex)\n                    {\n                        lblFileContent.Text = "Error reading file: " + ex.Message;\n                    }\n                }\n                else\n                {\n                    lblFileContent.Text = "File not found.";\n                }\n            }\n        }\n    }\n}