// Web Service (e.g., WebService.asmx.cs)\n[WebService(Namespace = "http://tempuri.org/")]\n[WebServiceBinding(ConformsTo = WsiProfiles.BasicProfile1_1)]\npublic class WebService : System.Web.Services.WebService\n{\n    private readonly DataPresenter _presenter;\n\n    public WebService()\n    {\n        _presenter = new DataPresenter(new DataModel());\n    }\n\n    [WebMethod]\n    public string SubmitData(string inputData)\n    {\n        return _presenter.ProcessData(inputData);\n    }\n}