using System;\nusing System.Windows;\nusing System.Windows.Controls;\nusing System.Windows.Interop;\nusing System.Windows.Navigation;\n\nnamespace WpfWebBrowserPrint\n{\n    public partial class MainWindow : Window\n    {\n        public MainWindow()\n        {\n            InitializeComponent();\n        }\n\n        private void PrintWebPage(object sender, RoutedEventArgs e)\n        {\n            dynamic activeX = webBrowser.GetType().InvokeMember("ActiveXInstance",\n                System.Reflection.BindingFlags.GetProperty | System.Reflection.BindingFlags.Instance | System.Reflection.BindingFlags.NonPublic,\n                null, webBrowser, new object[] { });\n\n            if (activeX != null)\n            {\n                activeX.ExecWB(7, 1); // 7 is the command for print, 1 is for showing the print dialog\n            }\n        }\n    }\n}