using System.Collections.Generic;\nusing System.IO;\nusing NGettext;\n\nclass Program\n{\n    static void Main()\n    {\n        var translations = new Dictionary<string, string>\n        {\n            { "Hello, world!", "Hola, mundo!" },\n            { "Goodbye!", "¡Adiós!" }\n        };\n\n        var poFile = new PoFile();\n        foreach (var translation in translations)\n        {\n            poFile.Add(new CatalogEntry(translation.Key, translation.Value));\n        }\n\n        using (var writer = new StreamWriter("translations.po"))\n        {\n            poFile.Save(writer);\n        }\n    }\n}