using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\n\npublic class Example\n{\n    public static void Main()\n    {\n        using (var client = new WebClient())\n        {\n            try\n            {\n                byte[] bytesToPost = Encoding.UTF8.GetBytes("yourDataHere");\n                byte[] bytesReceived = client.UploadData("http://localhost", bytesToPost);\n                string response = client.Encoding.GetString(bytesReceived);\n                Console.WriteLine(response);\n            }\n            catch (WebException webEx)\n            {\n                if (webEx.Response != null)\n                {\n                    using (var stream = webEx.Response.GetResponseStream())\n                    using (var reader = new StreamReader(stream))\n                    {\n                        string errorResponse = reader.ReadToEnd();\n                        Console.WriteLine("HTTP Error: " + errorResponse);\n                    }\n                }\n                else\n                {\n                    Console.WriteLine("WebException: " + webEx.Message);\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Exception: " + ex.Message);\n            }\n        }\n    }\n}