public static decimal RoundFractional(decimal value, int fraction)\n{\n    decimal factor = 1m / fraction;\n    return Math.Round(value / factor) * factor;\n}\n\n// Example usage:\ndecimal result1 = RoundFractional(1.9m, 8); // yields 1.875\ndecimal result2 = RoundFractional(1.95m, 8); // yields 2.0\n\nConsole.WriteLine(result1); // Output: 1.875\nConsole.WriteLine(result2); // Output: 2.0