using System;\nusing System.Collections.Generic;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string source = "This is a test. This test is only a test.";\n        string target = "test";\n\n        List<int> indexes = GetAllIndexes(source, target);\n\n        foreach (int index in indexes)\n        {\n            Console.WriteLine(index);\n        }\n    }\n\n    public static List<int> GetAllIndexes(string source, string target)\n    {\n        List<int> indexes = new List<int>();\n        int index = source.IndexOf(target);\n\n        while (index != -1)\n        {\n            indexes.Add(index);\n            index = source.IndexOf(target, index + target.Length);\n        }\n\n        return indexes;\n    }\n}