private void assignNames(DropDownList ddl, Hashtable names)\n{\n    List<ListItem> nameList = new List<ListItem>();\n    if (ddl != null)\n    {\n        ddl.ClearSelection();\n        ddl.Items.Add(new ListItem("Select Author"));\n        foreach (string key in names.Keys)\n        {\n            nameList.Add(new ListItem(names[key].ToString(), key));\n        }\n\n        // Sort the nameList by the Text property of ListItem\n        nameList.Sort((x, y) => string.Compare(x.Text, y.Text));\n        \n        // Add sorted items back to the DropDownList\n        foreach (var listItem in nameList)\n        {\n            ddl.Items.Add(listItem);\n        }\n    }\n}