using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string query = "select fieldslist from mytable where fieldvalue=someint order by specialfield";\n        string pattern = @"select\s+\w+\s+from\s+\w+\s+where\s+\w+\s*=\s*\w+\s+order\s+by\s+\w+";\n\n        Regex regex = new Regex(pattern, RegexOptions.IgnoreCase);\n        Match match = regex.Match(query);\n\n        if (match.Success)\n        {\n            Console.WriteLine("Query matches the pattern.");\n        }\n        else\n        {\n            Console.WriteLine("Query does not match the pattern.");\n        }\n    }\n}