using System;\nusing System.Management;\n\npublic class AntivirusDefinitionDate\n{\n    public static void Main(string[] args)\n    {\n        string remoteComputer = "RemoteComputerName"; // Replace with the target computer name\n        string wmiNamespace = @"\\\\" + remoteComputer + "\\root\\Symantec\\Symantec Endpoint Protection\\WMI";\n\n        try\n        {\n            ManagementScope scope = new ManagementScope(wmiNamespace);\n            scope.Connect();\n\n            ObjectQuery query = new ObjectQuery("SELECT * FROM VirusDefs");\n            ManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, query);\n            ManagementObjectCollection queryCollection = searcher.Get();\n\n            foreach (ManagementObject m in queryCollection)\n            {\n                Console.WriteLine("Virus Definitions Date: {0}", m["virusdefsdate"]);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}