using System;\nusing System.Globalization;\n\nclass Program\n{\n    static void Main()\n    {\n        // This is the desired format with the UTC 'Z' indicator\n        const string expected = "Fri Dec 19 17:24:18 +0000 2008";\n\n        // Create a UTC time\n        var time = new DateTime(2008, 12, 19, 17, 24, 18, 0, DateTimeKind.Utc);\n\n        // Format the date using 'u' and append the 'Z' to indicate UTC\n        var actual = time.ToString("ddd MMM dd HH:mm:ss yyyy", CultureInfo.InvariantCulture) + " +0000";\n\n        // Output the result\n        Console.WriteLine(actual);\n\n        // Assert the result (assuming this is part of a unit test)\n        if (expected != actual)\n        {\n            throw new Exception($"Expected '{expected}' but got '{actual}'");\n        }\n    }\n}