using System;\nusing System.IO;\nusing System.Reflection;\n\nclass Program\n{\n    static void Main()\n    {\n        // Replace 'YourNamespace' and 'YourImageName' with your actual namespace and image name\n        string resourceName = "YourNamespace.YourImageName.png";\n\n        // Obtain the assembly that contains the resource\n        Assembly assembly = Assembly.GetExecutingAssembly();\n\n        // Open the resource stream\n        using (Stream stream = assembly.GetManifestResourceStream(resourceName))\n        {\n            if (stream == null)\n            {\n                Console.WriteLine("Resource not found.");\n                return;\n            }\n\n            // Read the stream into a byte array\n            byte[] buffer = new byte[stream.Length];\n            stream.Read(buffer, 0, buffer.Length);\n\n            // Convert byte array to a readable string format (hex representation for example)\n            string rawData = BitConverter.ToString(buffer).Replace("-", " ");\n            Console.WriteLine(rawData);\n        }\n    }\n}