using System;\nusing System.IO;\nusing FAXCOMEXLib;\n\nnamespace ConsoleFaxTest\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            try\n            {\n                // Use absolute paths\n                string faxDocumentPath = @"C:\path\to\your\faxdocument.txt";\n                if (!File.Exists(faxDocumentPath))\n                {\n                    throw new FileNotFoundException("The fax document could not be found.", faxDocumentPath);\n                }\n\n                FaxServer faxServer = new FaxServer();\n                faxServer.Connect("");\n\n                FaxDocument faxDocument = new FaxDocument();\n                faxDocument.Body = faxDocumentPath;\n\n                object submitResult = faxDocument.ConnectedSubmit(faxServer);\n                Console.WriteLine("Fax submitted successfully.");\n                \n                faxServer.Disconnect();\n            }\n            catch (FileNotFoundException ex)\n            {\n                Console.WriteLine($"File not found: {ex.Message}");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Unhandled exception: {ex.Message}");\n            }\n        }\n    }\n}