using System;\nusing Microsoft.Reporting.WebForms;\n\nnamespace YourNamespace\n{\n    public partial class ReportViewerPage : System.Web.UI.Page\n    {\n        protected void Page_Load(object sender, EventArgs e)\n        {\n            if (!IsPostBack)\n            {\n                ConfigureReportViewer();\n            }\n        }\n\n        protected void RefreshButton_Click(object sender, EventArgs e)\n        {\n            ConfigureReportViewer();\n        }\n\n        private void ConfigureReportViewer()\n        {\n            ReportViewer1.ProcessingMode = ProcessingMode.Remote;\n            ReportViewer1.ServerReport.ReportServerUrl = new Uri("http://yourreportserver/reportserver");\n            ReportViewer1.ServerReport.ReportPath = "/YourReportPath/YourReportName";\n\n            // Add any parameters if required\n            ReportParameter[] parameters = new ReportParameter[1];\n            parameters[0] = new ReportParameter("YourParameterName", "ParameterValue");\n            ReportViewer1.ServerReport.SetParameters(parameters);\n\n            ReportViewer1.ServerReport.Refresh();\n        }\n    }\n}