public class NotesController : Controller\n{\n    private readonly IYourDbContext _context;\n\n    public NotesController(IYourDbContext context)\n    {\n        _context = context;\n    }\n\n    private bool ObjectExists(DataObjectType type, int id)\n    {\n        switch (type)\n        {\n            case DataObjectType.Customer:\n                return _context.Customers.Any(c => c.Id == id);\n            case DataObjectType.SalesLead:\n                return _context.SalesLeads.Any(sl => sl.Id == id);\n            // Add other cases as needed\n            default:\n                return false;\n        }\n    }\n\n    [HttpGet]\n    public ActionResult New(DataObjectType type, int id)\n    {\n        if (!ObjectExists(type, id))\n        {\n            return HttpNotFound("Object not found");\n        }\n\n        Note note = new Note { Type = type, Source = id };\n        return View(note);\n    }\n\n    [HttpPost]\n    public ActionResult Create(Note note)\n    {\n        if (!ObjectExists((DataObjectType)note.Type, note.Source))\n        {\n            return HttpNotFound("Object not found");\n        }\n\n        note.Persist();\n        return View("New", note);\n    }\n}