using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        string saltAndPassword = "yourSaltAndPassword"; // Replace with your actual salt and password concatenated\n\n        string hashedPwd = HashPasswordForStoringInConfigFile(saltAndPassword, "SHA1");\n        Console.WriteLine(hashedPwd);\n    }\n\n    public static string HashPasswordForStoringInConfigFile(string password, string hashAlgorithm)\n    {\n        HashAlgorithm algorithm = HashAlgorithm.Create(hashAlgorithm);\n        if (algorithm == null)\n        {\n            throw new ArgumentException("Invalid hash algorithm");\n        }\n\n        byte[] byteValue = Encoding.UTF8.GetBytes(password);\n        byte[] byteHash = algorithm.ComputeHash(byteValue);\n\n        StringBuilder sb = new StringBuilder();\n        foreach (byte b in byteHash)\n        {\n            sb.Append(b.ToString("x2"));\n        }\n        return sb.ToString();\n    }\n}