public class FileDataMap : ClassMap<FileData>\n{\n    public FileDataMap()\n    {\n        Id(x => x.Id);\n        HasOne(x => x.Person)\n            .PropertyRef(x => x.FileData)\n            .Cascade.All()\n            .Constrained();\n    }\n}\n\npublic class PersonMap : ClassMap<Person>\n{\n    public PersonMap()\n    {\n        Id(x => x.Id);\n        References(x => x.FileData)\n            .Unique()\n            .Not.Nullable();\n    }\n}