using System;\nusing System.Net.Mail;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.IO;\n\npublic void SendEmailWithSmtpClient(string from, string to, string subject, string content, string username, string password)\n{\n    using (TcpClient client = new TcpClient("smtp.example.com", 465))\n    using (SslStream sslStream = new SslStream(client.GetStream()))\n    {\n        sslStream.AuthenticateAsClient("smtp.example.com");\n\n        using (StreamWriter writer = new StreamWriter(sslStream))\n        using (StreamReader reader = new StreamReader(sslStream))\n        {\n            writer.WriteLine("EHLO smtp.example.com");\n            writer.Flush();\n            writer.WriteLine("AUTH LOGIN");\n            writer.Flush();\n            writer.WriteLine(Convert.ToBase64String(System.Text.Encoding.UTF8.GetBytes(username)));\n            writer.Flush();\n            writer.WriteLine(Convert.ToBase64String(System.Text.Encoding.UTF8.GetBytes(password)));\n            writer.Flush();\n            writer.WriteLine($"MAIL FROM: <{from}>");\n            writer.Flush();\n            writer.WriteLine($"RCPT TO: <{to}>");\n            writer.Flush();\n            writer.WriteLine("DATA");\n            writer.Flush();\n            writer.WriteLine($"Subject: {subject}\r\n\r\n{content}\r\n.");\n            writer.Flush();\n            writer.WriteLine("QUIT");\n            writer.Flush();\n        }\n    }\n}