// Initialize SubSonic query\nvar query = new SubSonic.SqlQuery();\n\n// Create an instance of MyPoco\nvar myPoco = new MyPoco(query)\n{\n    Id = 1,\n    Name = "Test",\n    ParentId = 2\n};\n\n// Accessing lazy-loaded properties\nvar children = myPoco.Children; // This will trigger the query to fetch children\nvar parent = myPoco.Parent;     // This will trigger the query to fetch parent