using System;\nusing System.Net;\nusing System.Net.Mail;\n\npublic class EmailSender\n{\n    public void SendEmail(string toAddress, string subject, string body)\n    {\n        try\n        {\n            string smtpServer = "mail.sortuv.com";\n            string fromAddress = "youremail@yourdomain.com";\n            string username = "yourusername";\n            string password = "yourpassword";\n            \n            MailMessage message = new MailMessage(fromAddress, toAddress, subject, body);\n            SmtpClient emailClient = new SmtpClient(smtpServer);\n\n            emailClient.UseDefaultCredentials = false;\n            emailClient.Credentials = new NetworkCredential(username, password);\n            emailClient.EnableSsl = true;\n            emailClient.Port = 587; // Common port for SMTP with SSL\n\n            emailClient.Send(message);\n        }\n        catch (Exception ex)\n        {\n            // Log the exception or handle it as needed\n            Console.WriteLine("Exception caught in SendEmail(): {0}", ex.ToString());\n        }\n    }\n}