public class MyPoco\n{\n    private SubSonic.SqlQuery _query;\n    private List<ChildPoco> _children;\n    private ParentPoco _parent;\n\n    public int Id { get; set; }\n    public string Name { get; set; }\n\n    // Constructor to initialize with a SubSonic query object\n    public MyPoco(SubSonic.SqlQuery query)\n    {\n        _query = query;\n    }\n\n    // Lazy loaded property for child collection\n    public List<ChildPoco> Children\n    {\n        get\n        {\n            if (_children == null)\n            {\n                _children = _query.From<ChildPoco>()\n                                  .Where("ParentId").IsEqualTo(this.Id)\n                                  .ExecuteTypedList<ChildPoco>();\n            }\n            return _children;\n        }\n    }\n\n    // Lazy loaded property for parent object\n    public ParentPoco Parent\n    {\n        get\n        {\n            if (_parent == null)\n            {\n                _parent = _query.From<ParentPoco>()\n                                .Where("Id").IsEqualTo(this.ParentId)\n                                .ExecuteSingle<ParentPoco>();\n            }\n            return _parent;\n        }\n    }\n\n    public int ParentId { get; set; }\n}