using System;\nusing System.IO;\nusing System.Web;\n\npublic class MyHttpModule : IHttpModule\n{\n    public void Init(HttpApplication context)\n    {\n        context.BeginRequest += new EventHandler(OnBeginRequest);\n    }\n\n    private void OnBeginRequest(object sender, EventArgs e)\n    {\n        HttpApplication app = (HttpApplication)sender;\n        string url = app.Request.Url.AbsolutePath;\n\n        // Check if the URL ends with a file\n        string extension = Path.GetExtension(url);\n        if (!string.IsNullOrEmpty(extension))\n        {\n            // Do something with the extension\n            // For example, log the file extension\n            app.Context.Response.Write($"File extension: {extension}");\n        }\n        else\n        {\n            app.Context.Response.Write("URL does not end with a file.");\n        }\n    }\n\n    public void Dispose()\n    {\n        // Clean-up code here if needed\n    }\n}