using (var context = new YourDataContext())\n{\n    var topFeeds = (from f in context.tblFeeds\n                    join s in context.tblSubscribers on f.id equals s.FeedId\n                    group f by new { f.id, f.FeedName } into feedGroup\n                    orderby feedGroup.Count() descending\n                    select new\n                    {\n                        FeedId = feedGroup.Key.id,\n                        FeedName = feedGroup.Key.FeedName,\n                        SubscriberCount = feedGroup.Count()\n                    }).Take(5);\n\n    foreach (var feed in topFeeds)\n    {\n        Console.WriteLine($"FeedId: {feed.FeedId}, FeedName: {feed.FeedName}, Subscribers: {feed.SubscriberCount}");\n    }\n}