[OperationContract]\n[WebInvoke(Method = "GET", UriTemplate = "/GetResponse", ResponseFormat = WebMessageFormat.Json)]\npublic Stream GetResponse()\n{\n    try\n    {\n        // Simulate a successful operation\n        var successResponse = new SuccessResponse { Message = "Operation completed successfully." };\n        return SerializeResponse(successResponse);\n    }\n    catch (Exception ex)\n    {\n        // Simulate an error condition\n        var errorResponse = new ErrorResponse { ErrorMessage = ex.Message };\n        throw new WebFaultException<ErrorResponse>(errorResponse, HttpStatusCode.InternalServerError);\n    }\n}\n\nprivate Stream SerializeResponse<T>(T response)\n{\n    WebOperationContext.Current.OutgoingResponse.ContentType = "application/json";\n    DataContractJsonSerializer serializer = new DataContractJsonSerializer(typeof(T));\n    MemoryStream memoryStream = new MemoryStream();\n    serializer.WriteObject(memoryStream, response);\n    memoryStream.Position = 0;\n    return memoryStream;\n}